import React, { Component } from "react";
import StatsCard from "./StatsCard";
import { Row, Col, Dropdown } from "react-bootstrap";
import AdminNavbar from "./AdminNavbar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import queryString from "query-string";
import moment from 'moment';

const mealUrl =
  "https://api.fitbit.com/1/user/-/foods/log/date/";
//2019-10-06.json
const foodUrl = "https://api.fitbit.com/1/foods/"

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mealLog: {},
      dataSummary: {},
      startDate: null
    };
    this.loadData = this.loadData.bind(this);
  }

  renderData()
  {
    if(Object.keys(this.state.dataSummary).length === 0)
    {
      return <h4>No logs available for that date</h4>
    }
      return  <Col lg={3} sm={6}>     {Object.keys(this.state.dataSummary).forEach(data => { 

<StatsCard
bigIcon={<i className="pe-7s-server text-warning" />}
statsText="Capacity"
statsValue={this.state.dataSummary[data]}
statsIcon={<i className="fa fa-refresh" />}
statsIconText={data}
/>

})} </Col> 

  }

  loadData(date)
  {
    //this.setState({startDate: date});
    console.log(date);
    let parsed = queryString.parse(window.location.search);
    let access_token = parsed.access_token;

    fetch(mealUrl+date+".json", 
      {headers : {'Authorization': 'Bearer '+access_token}
    }).then(response => response.json()).then(data => console.log(data.summary)
    )
  }

  componentDidMount() {
  }


  render() {
    return (
      <div className="main-panel" id="main-panel">
        <AdminNavbar data={this.props.userDetails} />
        <div className="content">
          <div className="container">
            <Row>
              <Col lg={4}>
                <Dropdown>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Select Food Filter
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">
                      Another action
                    </Dropdown.Item>
                    <Dropdown.Item href="#/action-3">
                      Something else
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Col>
              <Col lg={4}>
                <DatePicker
                  selected={this.state.startDate}
                  onChange={date => this.loadData(moment(date).format('YYYY-MM-DD'))}
                  dateFormat="yyyy-MM-dd"
                  placeholderText="Select food log date"
                /> 
              </Col>
            </Row>
            <br></br>
            <Row>


            </Row>
          </div>
        </div>
      </div>
    );
  }
}
