import React, {Component} from 'react';
import './App.css';
import Login from './Components/login';
import Home from './Components/home';
import queryString from "query-string";

const userUrl = "https://api.fitbit.com/1/user/-/profile.json";

class App extends Component {
  constructor(props) 
  {
    super(props);
    this.state = {
      userData : {},
      accessToken : ''
    }
  }
  componentDidMount()
  {
    let parsed = queryString.parse(window.location.search);
    let access_Token = parsed.access_token;
    //console.log(accessToken);
    //GET https://api.fitbit.com/1/user/[user-id]/profile.json
    fetch(userUrl, 
    {headers : {'Authorization': 'Bearer '+accessToken}
  }).then(response => response.json()).then(data => this.setState({userData : data.user, accessToken: access_Token}));

  }
  render () {

    return (
      <div>
    {this.state.userData ? <Home userDetails= {this.state.userData} /> : <Login/>}
      </div>


  );
  }

}

export default App;
