import React, { Component } from "react";
import StatsCard from "./StatsCard";
import { Row, Col, Dropdown } from "react-bootstrap";
import AdminNavbar from "./AdminNavbar";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import queryString from "query-string";

const mealUrl =
  "https://api.fitbit.com/1/user/-/foods/log/date/2019-10-06.json";

const foodUrl = "https://api.fitbit.com/1/foods/"

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.setState = {
      mealLog: {},
      dataSummary: {},
      startDate: new Date()
    };
  }

  componentDidMount() {
    let parsed = queryString.parse(window.location.search);
    let access_token = parsed.access_token;

    fetch(mealUrl, 
      {headers : {'Authorization': 'Bearer '+access_token}
    }).then(response => response.json()).then(data => this.setState({dataSummary : data.summary})
    )
  }


  render() {
    return (
      <div className="main-panel" id="main-panel">
        <AdminNavbar data={this.props.userDetails} />
        <div className="content">
          <div className="container">
            <Row>
              <Col lg={4}>
                <Dropdown>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Select Food Filter
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">
                      Another action
                    </Dropdown.Item>
                    <Dropdown.Item href="#/action-3">
                      Something else
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Col>
              <Col lg={4}>
                {/* <DatePicker
                  selected={this.state.startDate}
                  onChange={date => this.setState({ startDate: date })}
                  name="startDate"
                  dateFormat="MM/dd/yyyy"
                /> */}
              </Col>
            </Row>
            <br></br>
            <Row>
              <Col lg={3} sm={6}>
                <StatsCard
                  bigIcon={<i className="pe-7s-server text-warning" />}
                  statsText="Capacity"
                  statsValue="500"
                  statsIcon={<i className="fa fa-refresh" />}
                  statsIconText="Updated now"
                />
              </Col>
              <Col lg={3} sm={6}>
                <StatsCard
                  bigIcon={<i className="pe-7s-wallet text-success" />}
                  statsText="Revenue"
                  statsValue="$1,345"
                  statsIcon={<i className="fa fa-calendar-o" />}
                  statsIconText="Last day"
                />
              </Col>
              <Col lg={3} sm={6}>
                <StatsCard
                  bigIcon={<i className="pe-7s-graph1 text-danger" />}
                  statsText="Errors"
                  statsValue="23"
                  statsIcon={<i className="fa fa-clock-o" />}
                  statsIconText="In the last hour"
                />
              </Col>
              <Col lg={3} sm={6}>
                <StatsCard
                  bigIcon={<i className="fa fa-twitter text-info" />}
                  statsText="Followers"
                  statsValue="+45"
                  statsIcon={<i className="fa fa-refresh" />}
                  statsIconText="Updated now"
                />
              </Col>
            </Row>
          </div>
        </div>
      </div>
    );
  }
}
