

import * as React from 'react-bootstrap/node_modules/react';

import Nav, { NavProps } from 'react-bootstrap/Nav';

import {
  BsPrefixComponent,
  SelectCallback,
  BsPrefixComponentClass,
} from 'react-bootstrap/helpers';

export interface TabsProps {
  activeKey?: unknown;
  defaultActiveKey?: unknown;
  onSelect?: SelectCallback;
  variant?: 'tabs' | 'pills';
  transition?: false | React.ElementType;
  id: string;
  mountOnEnter?: boolean;
  unmountOnExit?: boolean;
}

declare class Tabs<
  // Need to use BsPrefixComponentClass to get proper type checking.
  As extends React.ElementType = BsPrefixComponentClass<'div', NavProps>
> extends BsPrefixComponent<As, TabsProps> {}

export default Tabs;
